import HomePage from '../views/HomePage.vue';
import LoginPage from '../views/LoginPage.vue';
import ProductsPage from '../views/ProductsPage.vue';
import RegistrationPage from '../views/RegistrationPage.vue';
import { createRouter, createWebHistory } from 'vue-router';
import axios from 'axios';

const routes = [
  {
    path: '/',
    name: 'Home',
    component: HomePage,
  },
  {
    path: '/login',
    name: 'Login',
    component: LoginPage,
  },
    {
    path: '/catalog',
    name: 'Catalog',
    component: ProductsPage,
  },
    {
    path: '/registration',
    name: 'Registration',
    component: RegistrationPage,
  },
];

async function checkAuthentication() {
  try {
    const response = await axios.get('http://127.0.0.1:5000/api/users/user/status', { withCredentials: true });
    return response.status === 200;
  } catch (error) {
    return false; // Не аутентифицирован
  }
}

// Создаем маршрутизатор
const router = createRouter({
  history: createWebHistory(),
  routes,
});

// Глобальный навигационный охранник
router.beforeEach(async (to, from, next) => {
  const isAuthenticated = await checkAuthentication();
  if (to.path !== '/login' && !isAuthenticated) {
    next('/login'); // Перенаправление на страницу входа, если не аутентифицирован
  } else {
    next(); // Продолжить навигацию
  }
});

export default router;
